import styled from "styled-components";

export const HomeBody = styled.div`
  background-image: url(/images/homeBG.svg);
  background-repeat: no-repeat;
  background-size: 100vw;
  height: 200vh;;
  width: 100vw;
  margin: 0;
  top:0;
`;

export const ActionContainer = styled.div`
  color: white;
  right: 10px;
  display: flex;
  position: absolute;
  div {
    margin: 10px 16px 0 16px;
  }
`;

export const HeaderContainer = styled.div`
  display: flex;
  justify-content: space-between;
  margin: 30vh 12vw 30vh 5vw;
  color: white;
  .title {
    font-size: 30vh;
  }
  .moto {
    display: flex;
    font-size: 5vh;
    .icon {
      margin: 0 6px;
    }
  }
  .Logo-wrapper {
    width: 40vh;
    height: 40vh;
    position: relative;
    overflow: hidden;
    border-radius: 50%;
  }
  .logo {
    display: inline;
    margin: 0 auto;
    height: 100%;
    width: auto;
  }
`;

export const AboutContainer = styled.div`
  .about-wrapper {
    width: 40vh;
    height: 40vh;
    position: relative;
    overflow: hidden;
  }
  .about {
    display: inline;
    margin: 0 auto;
    height: 100%;
    width: auto;
  }

`;
