import React, { useState } from "react";
import {
  HomeBody,
  ActionContainer,
  HeaderContainer,
  AboutContainer,
  Footer,
  CustomModal,
  CustomTabs,
  CustomInput,
  CustomInputPassword,
  SignInButton,
  CustomForm,
} from "./styles";
import {
  LockOutlined,
  MessageOutlined,
  PhoneOutlined,
  UserOutlined,
} from "@ant-design/icons";
import { Button, Checkbox } from "antd";
const { TabPane } = CustomTabs;

const Home = () => {
  const [isModalVisible, setModalShow] = useState(false);
  const [activeTab, setActiveTab] = useState("login");

  const showLoginModal = () => {
    setModalShow(true);
    setActiveTab("login");
    console.log("hey");
  };
  const showSignUpModal = () => {
    setModalShow(true);
    setActiveTab("signup");
  };
  const hideModal = () => {
    setModalShow(false);
  };
  const handleAction = () => {
    if (activeTab === "login") {
      handleLogin();
    } else {
      handleSignUp();
    }
  };
  const handleLogin = () => {
    // API Call for login
  };

  const handleSignUp = () => {
    // API Call for SignUp
  };
  const handleTabChange = (key) => {
    setActiveTab(key);
  };

  const onFinish = (values: any) => {
    console.log("Success:", values);
  };

  const onFinishFailed = (errorInfo: any) => {
    console.log("Failed:", errorInfo);
  };

  return (
    <HomeBody>
      <ActionContainer>
        <div onClick={showSignUpModal}>Sign Up</div>
        <div onClick={showLoginModal}>Login</div>
      </ActionContainer>
      <HeaderContainer>
        <div>
          <div className="title">Aaya</div>
          <div className="moto">moto</div>
        </div>
        <div>
          <div className="Logo-wrapper">
            <img src="https://picsum.photos/600" className="logo" alt="logo" />
          </div>
        </div>
      </HeaderContainer>
      <AboutContainer>
        <div className="about-wrapper">
          <img src="https://picsum.photos/800" className="about" alt="about" />
        </div>
        <div className="about-us">
          <div className="title">About Us</div>
          <div className="content">
            Lorem Ipsum is simply dummy text of the printing and typesetting
            industry. Lorem Ipsum has been the industry's standard dummy text
            ever sLorem Ipsum is simply dummy text of the printing and
            typesetting industry. Lorem Ipsum has been the industry's standard
            dummy text ever sLorem Ipsum is simply dummy text of the printing
            and typesetting industry. Lorem Ipsum has been the industry's
            standard dummy text ever s
          </div>
        </div>
      </AboutContainer>
      <Footer>
        <div>© LazySprinters 2021 </div>
        <div>Terms and Condition</div>
      </Footer>
      {isModalVisible && (
        <CustomModal
          style={{ top: 300 }}
          visible={isModalVisible}
          onCancel={hideModal}
          footer={null}
          width={600}
        >
          <CustomTabs defaultActiveKey={activeTab} onChange={handleTabChange}>
            <TabPane tab="Login" key="login">
              <CustomForm
                name="basic"
                labelCol={{ span: 8 }}
                wrapperCol={{ span: 16 }}
                initialValues={{ remember: true }}
                onFinish={onFinish}
                onFinishFailed={onFinishFailed}
                autoComplete="off"
              >
                <CustomForm.Item
                  name="username"
                  rules={[
                    { required: true, message: "Please input your username!" },
                  ]}
                >
                  <CustomInput
                    placeholder="Enter your username"
                    prefix={<UserOutlined className="prefix-icons" />}
                  />
                </CustomForm.Item>

                <CustomForm.Item
                  name="password"
                  rules={[
                    { required: true, message: "Please input your password!" },
                  ]}
                >
                  <CustomInputPassword
                    placeholder="Enter your password"
                    prefix={<LockOutlined className="prefix-icons" />}
                  />
                </CustomForm.Item>

                <CustomForm.Item className="actions">
                  <CustomForm.Item
                    name="remember"
                    valuePropName="checked"
                    noStyle
                    className="checkbox"
                  >
                    <Checkbox>Remember me</Checkbox>
                  </CustomForm.Item>

                  <a className="forget" href="">
                    Forgot your password ?
                  </a>
                </CustomForm.Item>

                <CustomForm.Item>
                  <SignInButton type="primary" htmlType="submit">
                    Log in
                  </SignInButton>
                </CustomForm.Item>
              </CustomForm>
            </TabPane>
            <TabPane tab="Sign Up" key="signup">
              <CustomForm
                name="basic"
                labelCol={{ span: 8 }}
                wrapperCol={{ span: 16 }}
                initialValues={{ remember: true }}
                onFinish={onFinish}
                onFinishFailed={onFinishFailed}
                autoComplete="off"
              >
                <CustomForm.Item
                  name="username"
                  rules={[
                    { required: true, message: "Please input your username!" },
                  ]}
                >
                  <CustomInput
                    placeholder="Enter your username"
                    prefix={<PhoneOutlined className="prefix-icons" />}
                  />
                </CustomForm.Item>

                <CustomForm.Item
                  name="password"
                  rules={[
                    { required: true, message: "Please input your password!" },
                  ]}
                >
                  <CustomInputPassword
                    placeholder="Enter your password"
                    prefix={<MessageOutlined className="prefix-icons" />}
                  />
                </CustomForm.Item>

                <CustomForm.Item>
                  <SignInButton type="primary" htmlType="submit">
                    Log in
                  </SignInButton>
                </CustomForm.Item>
              </CustomForm>
            </TabPane>
          </CustomTabs>
        </CustomModal>
      )}
    </HomeBody>
  );
};

export default Home;
