import { UploadOutlined } from "@ant-design/icons";
import { Button, Checkbox, DatePicker, Upload } from "antd";
import React, { useState } from "react";
import TnC from "../../components/Modals/TnC";
import { Footer } from "../../pages/Home/styles";
import {
  ActionContainer,
  CustomForm,
  CustomInput,
  CustomInputNumber,
  FormBody,
  FormTitle,
  FormWrapper,
  SubmitButton,
} from "../styles";

const plainOptions = ["Nanny", "Nurse"];

const RegisterServiceProvider = () => {
  const [checkedList, setCheckedList] = React.useState([]);
  const [isModalVisible, setModalVisible] = useState(false);
  const onChange = (list) => {
    setCheckedList(list);
  };

  const onFinishFailed = (errorInfo) => {
    console.log("Failed:", errorInfo);
  };
  const handleRegister = (values) => {
    //API to register
    const formattedValues = {
      ...values,
      dob: values["dob"].format("YYYY-MM-DD"),
    };
    console.log(formattedValues);
    setModalVisible(true);
  };
  const hideModal = () => {
    setModalVisible(false);
  };
  const handleTnC = () => {
    setModalVisible(false);
  };
  return (
    <FormBody>
      <ActionContainer>
        <img src="/images/navLogo.svg" alt="logo" />
        <div>Logout</div>
      </ActionContainer>
      <FormTitle>Enter your Details</FormTitle>

      <FormWrapper>
        <CustomForm
          name="basic"
          labelCol={{ span: 8 }}
          wrapperCol={{ span: 16 }}
          initialValues={{ remember: true }}
          onFinish={handleRegister}
          onFinishFailed={onFinishFailed}
          autoComplete="off"
        >
          <CustomForm.Item
            name="name"
            label="Name"
            rules={[{ required: true, message: "Please input your name!" }]}
          >
            <CustomInput placeholder="Enter your name" />
          </CustomForm.Item>

          <CustomForm.Item name="dob" label="DOB">
            <DatePicker />
          </CustomForm.Item>
          <CustomForm.Item name="email" label="Email">
            <CustomInput />
          </CustomForm.Item>
          <CustomForm.Item name="password" label="Password">
            <CustomInput.Password />
          </CustomForm.Item>
          <CustomForm.Item
            name="phoneNumber"
            label="Phone Number"
            rules={[
              { required: true, message: "Please input your phone number!" },
            ]}
          >
            <CustomInput style={{ width: "100%" }} />
          </CustomForm.Item>
          <CustomForm.Item name="address" label="Address">
            <CustomInput.TextArea />
          </CustomForm.Item>
          <CustomForm.Item name="pinCode" label="PinCode">
            <CustomInputNumber style={{ width: "100%" }} />
          </CustomForm.Item>
          <CustomForm.Item label="Local Police Station No.">
            <CustomInputNumber />
          </CustomForm.Item>
          <CustomForm.Item
            name="emergency"
            label="Emergency Number"
            rules={[
              {
                required: true,
                message: "Please input your Emergency number!",
              },
            ]}
          >
            <CustomInput style={{ width: "100%" }} />
          </CustomForm.Item>
          <CustomForm.Item label="Sign Up as: " className="sign-up-options">
            <Checkbox.Group
              options={plainOptions}
              value={checkedList}
              onChange={onChange}
            />
          </CustomForm.Item>
          <CustomForm.Item label="Aadhar Card">
            <Upload>
              <Button icon={<UploadOutlined />}>Click to Upload</Button>
            </Upload>
          </CustomForm.Item>
          <CustomForm.Item label="Your Degree/Certificate">
            <Upload maxCount={1}>
              <Button icon={<UploadOutlined />}>Click to Upload</Button>
            </Upload>
          </CustomForm.Item>
          <CustomForm.Item label="Passport size picture">
            <Upload maxCount={1}>
              <Button icon={<UploadOutlined />}>Click to Upload</Button>
            </Upload>
          </CustomForm.Item>
          <CustomForm.Item>
            <SubmitButton type="primary" htmlType="submit">
              Submit
            </SubmitButton>
          </CustomForm.Item>
        </CustomForm>
      </FormWrapper>
      <Footer>
        <div>© LazySprinters 2021 </div>
        <div>Terms and Condition</div>
      </Footer>
      {isModalVisible && (
        <TnC
          isModalVisible={isModalVisible}
          hideModal={hideModal}
          handleTnC={handleTnC}
        />
      )}
    </FormBody>
  );
};

export default RegisterServiceProvider;
